query Domain(
    $_id: String, 
    $name: String, 
    $activated: Boolean, 
    $environment: String, 
    $_component: String) {
    domain(
        _id: $_id, 
        name: $name, 
        activated: $activated, 
        environment: $environment,
         _component: $_component
    ) {
        _id
        name
        version
        description
        statusByEnv {
            env
            value
        }
        activated
        group {
            _id
            name
            description
            activated
            statusByEnv {
                env
                value
            }
            config {
                _id
                key
                description
                activated
                statusByEnv {
                    env
                    value
                }
                strategies {
                    _id
                    strategy
                    activated
                    statusByEnv {
                        env
                        value
                    }
                    operation
                    values
                }
                relay {
                    type
                    method
                    endpoint
                    description
                    activated
                    statusByEnv {
                        env
                        value
                    }
                }
                components
            }
        }
    }
}

