query Domain(
    $_id: String
    ) {
    domain(
        _id: $_id
    ) {
        _id
        name
        version
        description
        statusByEnv {
            env
            value
        }
        activated
        group {
            _id
            name
            description
            activated
            statusByEnv {
                env
                value
            }
            config {
                _id
                key
                description
                activated
                statusByEnv {
                    env
                    value
                }
                components
            }
        }
    }
}

query Domain(
    $_id: String,
    $environment: String
    ) {
    domain(
        _id: $_id,
        environment: $environment
    ) {
        _id
        name
        version
        description
        activated
        group {
            _id
            name
            description
            activated
            config {
                _id
                key
                description
                activated
                strategies {
                    _id
                    strategy
                    activated
                    operation
                    values
                }
                relay {
                    type
                    method
                    endpoint
                    description
                    activated
                }
                components
            }
        }
    }
}

