query Configuration(
    $domain: String!, 
    $group: String,
    $group_id: String,
    $key: String,
    $config_id: String,
    $slack_team_id: String,
    $environment: String) {
    configuration(domain: $domain, group: $group, group_id: $group_id, key: $key, config_id: $config_id, slack_team_id: $slack_team_id, environment: $environment) {
        domain {
            id: _id
            name
            owner
            transfer
            activated
            integrations {
                slack
