query Permission(
    $domain: String!, $actions: [String]!, $router: String!, 
    $parent: String, $environment: String) {
    permission(
        domain: $domain, 
        parent: $parent, 
        actions: $actions,
        router: $router,
        environment: $environment
    ) {
        id
        name
        permissions {
            action
            result
        }
    }
}

